import React from 'react';
import { Bar } from 'react-chartjs-2';

const Dashboard = ({ stats }) => {
    // Calculate total sum of stats
    const total = stats.clients + stats.unpaid + stats.revenue + stats.anomalie + stats.reclamation;

    return (
        <div className="dashboard" style={{ textAlign: 'center', marginTop: '10px', margin: 'auto', maxWidth: '800px' }}>
            <h1 style={{ fontSize: '36px', marginBottom: '20px' }}>Dashboard</h1>
            <CircularProgress
                variant="determinate"
                value={(total / 500) * 100} // Assuming 500 is the maximum total value, adjust as needed
                size={200} // Adjust the size of the circular progress
                thickness={4} // Adjust the thickness of the circular progress
            />
        </div>
    );
}

export default Dashboard;
