import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';

const Dashboard = ({ stats }) => {
    const barChartData = {
        labels: ['Clients', 'Unpaid', 'Revenue', 'Anomalie', 'Reclamation'],
        datasets: [
            {
                label: 'Statistics',
                backgroundColor: 'rgba(75,192,192,1)',
                borderColor: 'rgba(0,0,0,1)',
                borderWidth: 2,
                data: [stats.clients, stats.unpaid, stats.revenue, stats.anomalie, stats.reclamation]
            }
        ]
    };

    const pieChartData = {
        labels: ['Clients', 'Other'],
        datasets: [
            {
                data: [stats.clients, 100 - stats.clients],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.6)',
                    'rgba(54, 162, 235, 0.6)',
                ],
                borderColor: 'rgba(0,0,0,1)',
                borderWidth: 2,
            }
        ]
    };

    return (
        <div className="dashboard" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginTop: '50px' }}>
            <div style={{ marginRight: '50px' }}>
                <h1 style={{ fontSize: '36px', marginBottom: '20px' }}>Grand Dashboard</h1>
                <Bar
                    data={barChartData}
                    options={{
                        title: {
                            display: true,
                            text: 'Statistics',
                            fontSize: 20
                        },
                        legend: {
                            display: true,
                            position: 'right'
                        }
                    }}
                />
            </div>
            <div>
                <h2 style={{ fontSize: '24px', marginBottom: '20px' }}>Client Distribution</h2>
                <Pie
                    data={pieChartData}
                    options={{
                        title: {
                            display: true,
                            text: 'Client Distribution',
                            fontSize: 16
                        },
                        legend: {
                            display: true,
                            position: 'right',
                            labels: {
                                fontSize: 12
                            }
                        },
                        responsive: true,
                        maintainAspectRatio: false
                    }}
                    width={200}
                    height={200}
                />
            </div>
        </div>
    );
}

export default Dashboard;
