import React from 'react';
import { Bar } from 'react-chartjs-2';

const Dashboard = ({ stats }) => {
    const data = {
        labels: ['Clients', 'Unpaid', 'Revenue', 'Anomalie', 'Reclamation', 'NewStatistic'], // Include the new statistic label
        datasets: [
            {
                label: 'Statistics',
                backgroundColor: [
                    'rgb(0,139,202)',
                    'rgb(255,99,132)', // Color for the new statistic
                    'rgb(0,128,0)', // Color for the new statistic
                    'rgb(255,205,86)', // Color for the new statistic
                    'rgb(75,192,192)', // Color for the new statistic
                    'rgb(54,162,235)' // Color for the new statistic
                ],
                borderColor: 'rgba(0,0,0,1)',
                borderWidth: 2,
                data: [stats.clients, stats.unpaid, stats.revenue, stats.anomalie, stats.reclamation, stats.newStatistic] // Add the new statistic data
            }
        ]
    };
    

    return (
        <div className="dashboard" style={{ textAlign: 'center', marginTop: '10px', margin: 'auto', maxWidth: '600px' }}>
            <h1 style={{ fontSize: '36px', marginBottom: '20px' }}>Dashboard</h1>
            <Bar
                data={data}
                options={{
                    title: {
                        display: true,
                        text: 'Statistics',
                        fontSize: 20
                    },
                    legend: {
                        display: true,
                        position: 'right'
                    }
                }}
            />
        </div>
    );
}

export default Dashboard;
