import React from 'react';
import { Bar } from 'react-chartjs-2';

const Dashboard = ({ stats }) => {
    const data = {
        labels: ['Clients', 'Unpaid', 'Revenue', 'Anomalie', 'Reclamation'],
        datasets: [
            {
                label: 'Statistics',
                backgroundColor: [
                    'rgba(255, 99, 132, 0.6)',
                    'rgba(54, 162, 235, 0.6)',
                    'rgba(255, 206, 86, 0.6)',
                    'rgba(75, 192, 192, 0.6)',
                    'rgba(153, 102, 255, 0.6)'
                ],
                borderColor: 'rgba(0,0,0,1)',
                borderWidth: 2,
                hoverBackgroundColor: [
                    'rgba(255, 99, 132, 0.8)',
                    'rgba(54, 162, 235, 0.8)',
                    'rgba(255, 206, 86, 0.8)',
                    'rgba(75, 192, 192, 0.8)',
                    'rgba(153, 102, 255, 0.8)'
                ],
                hoverBorderColor: 'rgba(0,0,0,1)',
                data: [stats.clients, stats.unpaid, stats.revenue, stats.anomalie, stats.reclamation]
            }
        ]
    };

    return (
        <div className="dashboard" style={{ maxWidth: '600px', margin: '0 auto' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Grand Dashboard</h2>
            <Bar
                data={data}
                options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    scales: {
                        yAxes: [{
                            ticks: {
                                beginAtZero: true
                            }
                        }]
                    },
                    title: {
                        display: true,
                        text: 'Statistics',
                        fontSize: 24
                    },
                    legend: {
                        display: true,
                        position: 'bottom',
                        labels: {
                            fontSize: 16,
                            boxWidth: 20
                        }
                    },
                    animation: {
                        duration: 2000,
                        easing: 'easeInOutQuad'
                    }
                }}
            />
        </div>
    );
}

export default Dashboard;
